static size_t azk_generated_card_defs_count(void) {
    return sizeof(kGeneratedCardDefs) / sizeof(kGeneratedCardDefs[0]);
}

static void azk_apply_type_tag(ecs_world_t *world, ecs_entity_t entity, CardType type) {
    switch (type) {
    case CARD_TYPE_LEADER:
        ecs_add(world, entity, TLeader);
        break;
    case CARD_TYPE_GATE:
        ecs_add(world, entity, TGate);
        break;
    case CARD_TYPE_ENTITY:
        ecs_add(world, entity, TEntity);
        break;
    case CARD_TYPE_WEAPON:
        ecs_add(world, entity, TWeapon);
        break;
    case CARD_TYPE_SPELL:
        ecs_add(world, entity, TSpell);
        break;
    case CARD_TYPE_IKZ:
        ecs_add(world, entity, TIKZ);
        break;
    default:
        break;
    }
}

void azk_register_generated_card_defs(ecs_world_t *world) {
    const size_t count = azk_generated_card_defs_count();
    for (size_t index = 0; index < count; ++index) {
        const CardDef *def = &kGeneratedCardDefs[index];
        ecs_entity_t card = ecs_entity_init(world, &(ecs_entity_desc_t){
            .name = def->card_id,
        });

        if (def->name && def->name[0] != '\0') {
            ecs_set_symbol(world, card, def->name);
        }

        azk_apply_type_tag(world, card, def->type);

        Element element_component = { .element = (uint8_t)def->element };
        ecs_set_ptr(world, card, Element, &element_component);

        if (def->has_base_stats) {
            ecs_set_ptr(world, card, BaseStats, &def->base_stats);
        }

        if (def->has_gate_points) {
            ecs_set_ptr(world, card, GatePoints, &def->gate_points);
        }

        if (def->has_ikz_cost) {
            ecs_set_ptr(world, card, IKZCost, &def->ikz_cost);
        }
    }
}

const CardDef *azk_card_defs_data(size_t *out_count) {
    if (out_count) {
        *out_count = azk_generated_card_defs_count();
    }
    return kGeneratedCardDefs;
}

